INSERT INTO `emr` VALUES (61, '2014-03-01', '11:19:00', '胃炎', '上腹部不适嗳气4天余。', '患儿4天来出现上腹部不适，伴有嗳气，夜间明显。无明显腹痛，无反酸，无呕吐，昨日在我院门诊给于“吗丁啉、奥美拉唑”，无气急，无胸闷，无胸痛，大小便未见明显异常。', '无', '神志清楚，精神可，唇红，咽部充血，双肺呼吸音稍清，心音可，心律齐，无杂音，腹部软，不胀，未扪及包块，肝脾肋下未扪及，上腹部压痛明显，肠鸣音稍活跃。无异常神经体征。', '无。', ' 胃炎。', ' 注意饮食，服药见处方。不适随访。', '无。', '');
