INSERT INTO `emr` VALUES (242, '2012-11-19', '15:59:00', '发热', ' 头晕、畏寒，发热1天，伴全身酸痛、乏力。', ' 今天早晨起床时自觉发热，头晕，浑身酸痛，四肢乏力，无心慌胸闷，不咳嗽，无鼻塞流涕喷嚏，余无不适。', ' 既往体健，否认药物过敏史。', ' 腋下体温38.5度，咽部、扁桃体未见异常。', '无。', ' 感冒。', ' 对乙酰氨基酚片，感冒灵胶囊，多饮水，如有不适，随时就诊。', '无。', '');
