INSERT INTO `emr` VALUES (350, '2003-03-03', '14:22:00', '糖尿病', '糖尿病复诊配药。', '有糖尿病史两年,平时饮食控制尚可，一周前查空腹血糖5.99mmol/L，餐后两小时血糖10mmol/L。无不适，要求配药。', '有“高血压病”史5年。', 'BP120/80mmHg，神清，精神可，两肺呼吸音清。心率80次/分，律齐，无杂音。腹软，无压痛，肝脾未及，双下肢无浮肿，足背动脉搏动良好。', '无。', '无。', '用药详见处方', ' 不适随诊', '');
